INSERT INTO skills (name, department, level, class_level, men_code, description, chapter_tag, prerequisites, estimated_time_minutes, difficulty_rating, learning_objectives) VALUES
('Wartość miejsca w systemie dziesiętnym (do milionów)', 'arithmetic', 'basic', 4, null, 'Odczyt i zapis liczb do 7 cyfr, rozkład na sumę składowych miejsc.', 'Liczby naturalne', ARRAY[]::text[], 15, 1, ARRAY['Rozpoznaje wartość miejsca', 'Rozkłada liczbę na rozwinięcie dziesiętne', 'Porównuje na podstawie najwyższego miejsca']),
('Porównywanie i porządkowanie liczb naturalnych', 'arithmetic', 'basic', 4, null, 'Porównywanie liczb różnej długości zapisu i porządkowanie rosnąco/malejąco.', 'Liczby naturalne', ARRAY['Wartość miejsca w systemie dziesiętnym (do milionów)'], 15, 1, ARRAY['Porządkuje listy liczb', 'Zaznacza liczby na osi', 'Uzasadnia porównania']),
('Zaokrąglanie liczb naturalnych do dziesiątek, setek, tysięcy', 'arithmetic', 'basic', 4, null, 'Reguła 0–4 w dół i 5–9 w górę, zastosowanie do szacowania.', 'Liczby naturalne', ARRAY['Wartość miejsca w systemie dziesiętnym (do milionów)'], 15, 1, ARRAY['Zaokrągla do wskazanego miejsca', 'Szacuje wyniki działań', 'Ocena sensowności przybliżeń']),
('Dodawanie pisemne liczb naturalnych (z przeniesieniem)', 'arithmetic', 'basic', 4, null, 'Kolumnowe dodawanie z przeniesieniami w wyższe rzędy.', 'Działania pisemne', ARRAY['Porównywanie i porządkowanie liczb naturalnych'], 20, 2, ARRAY['Ustawia liczby w kolumnach', 'Wykonuje przeniesienia', 'Sprawdza wynik oszacowaniem']),
('Odejmowanie pisemne liczb naturalnych (z pożyczką)', 'arithmetic', 'basic', 4, null, 'Kolumnowe odejmowanie z pożyczką między miejscami.', 'Działania pisemne', ARRAY['Dodawanie pisemne liczb naturalnych (z przeniesieniem)'], 20, 2, ARRAY['Wykonuje pożyczki', 'Weryfikuje wyniki działaniem odwrotnym', 'Interpretuje błąd rachunkowy']),
('Mnożenie pisemne przez liczbę jednocyfrową', 'arithmetic', 'basic', 4, null, 'Mnożenie kolumnowe przez 2–9 z przeniesieniami.', 'Działania pisemne', ARRAY['Tabliczka mnożenia do 100'], 15, 2, ARRAY['Stosuje układ w kolumnie', 'Kontroluje przeniesienia', 'Oszacowuje rząd wielkości']),
('Mnożenie pisemne przez liczbę dwucyfrową', 'arithmetic', 'intermediate', 5, null, 'Metoda wierszy częściowych, dodawanie wyników cząstkowych.', 'Działania pisemne', ARRAY['Mnożenie pisemne przez liczbę jednocyfrową'], 20, 3, ARRAY['Wyznacza wiersze częściowe', 'Poprawnie sumuje częściowe', 'Kontroluje liczbę zer']),
('Dzielenie pisemne przez liczbę jednocyfrową (z resztą)', 'arithmetic', 'basic', 4, null, 'Dzielenie w słupku przez 2–9, rozumienie reszty.', 'Działania pisemne', ARRAY['Mnożenie pisemne przez liczbę jednocyfrową'], 20, 2, ARRAY['Wyznacza kolejne cyfry ilorazu', 'Oblicza i interpretuje resztę', 'Sprawdza wynik mnożeniem']),
('Dzielenie pisemne przez liczbę dwucyfrową (proste przypadki)', 'arithmetic', 'intermediate', 5, null, 'Dzielenie przez 10–99, estymacja kolejnych cyfr ilorazu.', 'Działania pisemne', ARRAY['Dzielenie pisemne przez liczbę jednocyfrową (z resztą)'], 25, 3, ARRAY['Szacuje cyfry ilorazu', 'Weryfikuje przez mnożenie', 'Analizuje sensowność wyniku']),
('Własności działań: przemienność i łączność (dodawanie, mnożenie)', 'arithmetic', 'basic', 4, null, 'Uproszczenia rachunkowe dzięki przemienności i łączności.', 'Własności działań', ARRAY['Dodawanie pisemne liczb naturalnych (z przeniesieniem)'], 15, 2, ARRAY['Grupuje składniki', 'Zmienia kolejność czynników', 'Porównuje strategie obliczeń']),
('Rozdzielność mnożenia względem dodawania i odejmowania', 'arithmetic', 'basic', 5, null, 'Zastosowanie rozdzielności do obliczeń w pamięci i upraszczania.', 'Własności działań', ARRAY['Własności działań: przemienność i łączność (dodawanie, mnożenie)'], 15, 2, ARRAY['Rozkłada czynnik na sumę', 'Upraszcza rachunki mentalne', 'Ocena błędów typowych']),
('Kolejność wykonywania działań z nawiasami', 'arithmetic', 'basic', 4, null, 'Reguła: nawiasy → mnożenie/dzielenie → dodawanie/odejmowanie.', 'Kolejność działań', ARRAY['Rozdzielność mnożenia względem dodawania i odejmowania'], 20, 2, ARRAY['Rozwiązuje wyrażenia wieloetapowe', 'Uzasadnia kolejność', 'Koryguje typowe błędy']),
('Czynniki i wielokrotności, parzystość i nieparzystość', 'arithmetic', 'basic', 4, null, 'Czynniki liczby i jej wielokrotności, pojęcie parzystości.', 'Podzielność liczb', ARRAY['Porównywanie i porządkowanie liczb naturalnych'], 15, 2, ARRAY['Wyznacza czynniki', 'Wyszukuje wielokrotności', 'Rozpoznaje parzystość po reszcie z dzielenia przez 2']),
('Reguły podzielności przez 2, 3, 5, 9, 10', 'arithmetic', 'basic', 5, null, 'Stosowanie prostych testów podzielności do szybkich wniosków.', 'Podzielność liczb', ARRAY['Czynniki i wielokrotności, parzystość i nieparzystość'], 15, 2, ARRAY['Zna testy podzielności', 'Weryfikuje podzielność liczb', 'Używa sumy cyfr dla 9 i 3']),
('Rozkład na czynniki pierwsze i zapis potęgowy', 'arithmetic', 'intermediate', 5, null, 'Rozkład metodą kratki/drzewa i zapis w notacji potęgowej.', 'Podzielność liczb', ARRAY['Reguły podzielności przez 2, 3, 5, 9, 10'], 20, 3, ARRAY['Dzieli przez kolejne liczby pierwsze', 'Zapisuje potęgowo', 'Weryfikuje rozkład mnożeniem']),
('NWD – największy wspólny dzielnik (rozkłady, algorytm Euklidesa)', 'arithmetic', 'intermediate', 5, null, 'Obliczanie NWD z rozkładu na czynniki i algorytmem Euklidesa.', 'NWD i NWW', ARRAY['Rozkład na czynniki pierwsze i zapis potęgowy'], 20, 3, ARRAY['Wyznacza NWD z rozkładów', 'Stosuje algorytm Euklidesa', 'Używa NWD do skracania ułamków']),
('NWW – najmniejsza wspólna wielokrotność (z rozkładów)', 'arithmetic', 'intermediate', 5, null, 'Wyznaczanie NWW przez dobór maksymalnych potęg czynników.', 'NWD i NWW', ARRAY['Rozkład na czynniki pierwsze i zapis potęgowy'], 20, 3, ARRAY['Dobiera potęgi czynników', 'Sprawdza relację NWD·NWW=a·b', 'Stosuje do wspólnego mianownika']),
('Liczby całkowite – wprowadzenie (dodatnie i ujemne)', 'real_numbers', 'basic', 6, null, 'Znaczenie liczb ujemnych (temperatura, długi, wysokości).', 'Liczby całkowite', ARRAY['Oś liczbowa – podstawy'], 15, 2, ARRAY['Zaznacza liczby całkowite na osi', 'Porównuje całkowite', 'Rozumie interpretacje kontekstowe']),
('Dodawanie i odejmowanie liczb całkowitych na osi', 'real_numbers', 'basic', 6, null, 'Ruchy na osi jako model dodawania/odejmowania liczb ujemnych.', 'Liczby całkowite', ARRAY['Liczby całkowite – wprowadzenie (dodatnie i ujemne)'], 20, 2, ARRAY['Modeluje działanie ruchem na osi', 'Stosuje zasady znaków', 'Weryfikuje wyniki przykładem']),
('Mnożenie i dzielenie liczb całkowitych – zasady znaków', 'real_numbers', 'intermediate', 6, null, 'Zasady znaków przy mnożeniu i dzieleniu (plus/minus).', 'Liczby całkowite', ARRAY['Dodawanie i odejmowanie liczb całkowitych na osi'], 20, 3, ARRAY['Stosuje zasady znaków', 'Rozwiązuje równania prostego typu', 'Analizuje kontrprzykłady']),
('Liczby dziesiętne – zapis, porównywanie i oś liczbowa', 'arithmetic', 'basic', 4, null, 'Miejsca dziesiętne i setne, porównywanie i zaznaczanie na osi.', 'Liczby dziesiętne', ARRAY['Wartość miejsca w systemie dziesiętnym (do milionów)'], 15, 2, ARRAY['Porównuje liczby dziesiętne', 'Porządkuje rosnąco/malejąco', 'Wyznacza pozycje na osi']),
('Dodawanie i odejmowanie liczb dziesiętnych (wyrównanie przecinka)', 'arithmetic', 'basic', 4, null, 'Ustawianie przecinków w kolumnach, przeniesienia i pożyczki.', 'Liczby dziesiętne – działania', ARRAY['Liczby dziesiętne – zapis, porównywanie i oś liczbowa'], 20, 2, ARRAY['Wyrównuje przecinki', 'Zaokrągla wynik', 'Kontroluje wynik oszacowaniem']),
('Mnożenie liczb dziesiętnych (liczenie miejsc po przecinku)', 'arithmetic', 'intermediate', 5, null, 'Mnożenie jak całkowitych, a potem ustawienie przecinka według sumy miejsc.', 'Liczby dziesiętne – działania', ARRAY['Mnożenie pisemne przez liczbę jednocyfrową'], 20, 3, ARRAY['Mnoży ignorując przecinek', 'Liczy liczbę miejsc po przecinku', 'Sprawdza oszacowaniem']),
('Dzielenie liczb dziesiętnych (przesunięcie przecinka w dzielniku)', 'arithmetic', 'intermediate', 5, null, 'Usuwanie przecinka z dzielnika i odpowiednie przesunięcie w dzielnej.', 'Liczby dziesiętne – działania', ARRAY['Dzielenie pisemne przez liczbę jednocyfrową (z resztą)'], 20, 3, ARRAY['Przesuwa przecinki poprawnie', 'Wyznacza miejsca w ilorazie', 'Sprawdza przez mnożenie']),
('Kolejność działań – wyrażenia z liczbami dziesiętnymi', 'arithmetic', 'intermediate', 5, null, 'Wyrażenia wieloetapowe z nawiasami i liczbami dziesiętnymi.', 'Kolejność działań', ARRAY['Kolejność wykonywania działań z nawiasami', 'Dodawanie i odejmowanie liczb dziesiętnych (wyrównanie przecinka)'], 20, 3, ARRAY['Stosuje reguły kolejności', 'Uzasadnia kroki obliczeń', 'Weryfikuje wyniki w kontekście']),
