INSERT INTO skills (name, department, level, class_level, men_code, description, chapter_tag, prerequisites, estimated_time_minutes, difficulty_rating, learning_objectives) VALUES
('Ułamki zwykłe – pojęcie i reprezentacje (część całości, odcinek, diagram)', 'arithmetic', 'basic', 4, null, 'Ułamek jako część całości i jako iloraz liczb naturalnych.', 'Ułamki zwykłe', ARRAY['Wartość miejsca w systemie dziesiętnym (do milionów)'], 20, 2, ARRAY['Odczytuje i zapisuje ułamki', 'Zaznacza na osi liczbowej', 'Wskazuje całość i część']),
('Ułamki równoważne i skracanie przez NWD', 'arithmetic', 'basic', 5, null, 'Rozpoznanie równoważności i skracanie ułamków przez NWD.', 'Ułamki zwykłe', ARRAY['NWD – największy wspólny dzielnik (rozkłady, algorytm Euklidesa)'], 20, 2, ARRAY['Wyznacza NWD licznika i mianownika', 'Skraca do nieskracalnej', 'Sprawdza równoważność']),
('Sprowadzanie do wspólnego mianownika (NWW mianowników)', 'arithmetic', 'intermediate', 5, null, 'Wspólny mianownik poprzez NWW – przygotowanie do dodawania/odejmowania.', 'Ułamki zwykłe', ARRAY['NWW – najmniejsza wspólna wielokrotność (z rozkładów)'], 20, 3, ARRAY['Dobiera NWW mianowników', 'Rozszerza ułamki', 'Kontroluje równoważność po rozszerzeniu']),
('Dodawanie i odejmowanie ułamków o wspólnym mianowniku', 'arithmetic', 'basic', 5, null, 'Operacje na licznikach, upraszczanie wyników.', 'Ułamki – działania', ARRAY['Ułamki zwykłe – pojęcie i reprezentacje (część całości, odcinek, diagram)'], 15, 2, ARRAY['Dodaje i odejmuje liczniki', 'Skraca wynik', 'Interpretuje operacje na modelu']),
('Dodawanie i odejmowanie ułamków o różnych mianownikach', 'arithmetic', 'intermediate', 5, null, 'Sprowadzanie do wspólnego mianownika i operacje na licznikach.', 'Ułamki – działania', ARRAY['Sprowadzanie do wspólnego mianownika (NWW mianowników)'], 20, 3, ARRAY['Sprowadza do NWW', 'Dodaje/odejmuje liczniki', 'Upraszcza wynik']),
('Mnożenie ułamków (w tym przez liczbę naturalną) – skracanie na krzyż', 'arithmetic', 'intermediate', 5, null, 'Mnożenie liczników i mianowników, skracanie na krzyż.', 'Ułamki – działania', ARRAY['Ułamki równoważne i skracanie przez NWD'], 20, 3, ARRAY['Mnoży ułamki i skraca', 'Interpretuje mnożenie przez naturalną', 'Weryfikuje wyniki']),
('Dzielenie ułamków – mnożenie przez odwrotność', 'arithmetic', 'intermediate', 5, null, 'Zamiana dzielenia na mnożenie przez odwrotność drugiego czynnika.', 'Ułamki – działania', ARRAY['Mnożenie ułamków (w tym przez liczbę naturalną) – skracanie na krzyż'], 20, 3, ARRAY['Stosuje odwrotność', 'Skraca na krzyż', 'Sprawdza wynik przez mnożenie']),
('Ułamki niewłaściwe i liczby mieszane – obustronne zamiany', 'arithmetic', 'basic', 4, null, 'Konwersja ułamków niewłaściwych i liczb mieszanych oraz zastosowania w zadaniach.', 'Ułamki zwykłe', ARRAY['Ułamki zwykłe – pojęcie i reprezentacje (część całości, odcinek, diagram)'], 15, 2, ARRAY['Rozkłada ułamek niewłaściwy', 'Zamienia liczbę mieszaną na ułamek', 'Stosuje w obliczeniach']),
('Liczby dziesiętne ↔ ułamki zwykłe (zamiany w obie strony)', 'arithmetic', 'basic', 5, null, 'Dzielenie licznika przez mianownik i zapis dziesiętny jako ułamek (10^k).', 'Ułamki ↔ dziesiętne', ARRAY['Dzielenie liczb dziesiętnych (przesunięcie przecinka w dzielniku)', 'Ułamki równoważne i skracanie przez NWD'], 15, 2, ARRAY['Zamienia przez dzielenie', 'Zapisuje dziesiętne jako ułamki', 'Skraca do nieskracalnej']),
('Stosunek, proporcja i skala – wprowadzenie i interpretacja', 'arithmetic', 'basic', 5, null, 'Zapis a:b=c:d, sprawdzanie prawdziwości proporcji, skale map.', 'Proporcje i skale', ARRAY['Mnożenie liczb dziesiętnych (liczenie miejsc po przecinku)'], 15, 2, ARRAY['Zapisuje proporcje', 'Sprawdza równoważność', 'Przelicza odległości w skali']),
('Proporcje – metoda na krzyż i uzupełnianie tabel', 'arithmetic', 'intermediate', 5, null, 'Rozwiązywanie proporcji metodą na krzyż, uzupełnianie braków w tabelach.', 'Proporcje i skale', ARRAY['Stosunek, proporcja i skala – wprowadzenie i interpretacja'], 20, 3, ARRAY['Uzupełnia tablice proporcji', 'Rozwiązuje na krzyż', 'Weryfikuje wyniki']),
('Jednostkowa cena, prędkość i gęstość – stawki jednostkowe', 'arithmetic', 'intermediate', 6, null, 'Obliczanie wielkości jednostkowych (zł/kg, km/h, zł/szt.).', 'Wielkości i stawki jednostkowe', ARRAY['Proporcje – metoda na krzyż i uzupełnianie tabel'], 20, 3, ARRAY['Oblicza cenę jednostkową', 'Porównuje oferty', 'Stosuje v=s/t i przelicza jednostki']),
('Procent – pojęcie i reprezentacje (diagramy, ułamek, liczba dziesiętna)', 'arithmetic', 'basic', 6, null, 'Procent jako ułamek o mianowniku 100 i jego reprezentacje.', 'Procenty', ARRAY['Liczby dziesiętne ↔ ułamki zwykłe (zamiany w obie strony)'], 20, 2, ARRAY['Zamienia % ↔ ułamek ↔ dziesiętne', 'Interpretuje 25%, 50%, 75%', 'Zaznacza na diagramach']),
('Obliczanie procentu z liczby (p% z N)', 'arithmetic', 'basic', 6, null, 'Metoda ułamka i metoda liczby dziesiętnej do liczenia p% z N.', 'Procenty', ARRAY['Procent – pojęcie i reprezentacje (diagramy, ułamek, liczba dziesiętna)'], 20, 2, ARRAY['Stosuje wzór p/100·N', 'Sprawdza wyniki estymacją', 'Dobiera jednostki i interpretację']),
('Jaka liczba stanowi p% innej liczby (odwrotne zadania procentowe)', 'arithmetic', 'intermediate', 6, null, 'Wyznaczanie liczby bazowej, gdy znany jest procent i wartość procentowa.', 'Procenty', ARRAY['Obliczanie procentu z liczby (p% z N)'], 20, 3, ARRAY['Rozwiązuje x·p/100=A', 'Stosuje proporcję', 'Weryfikuje wynik']),
('Porównania procentowe – o ile procent większa/mniejsza', 'arithmetic', 'intermediate', 6, null, 'Różnice względne i asymetria wzrostu/spadku w %.', 'Procenty', ARRAY['Obliczanie procentu z liczby (p% z N)'], 20, 3, ARRAY['Stosuje (B−A)/A·100%', 'Rozumie asymetrię spadku vs wzrostu', 'Interpretuje wykresy i tabele']),
('Procent składany – 2–3 następujące po sobie zmiany', 'arithmetic', 'intermediate', 6, null, 'Mnożniki (1±p/100) i ich składanie w łańcuchu zmian.', 'Procenty', ARRAY['Porównania procentowe – o ile procent większa/mniejsza'], 25, 3, ARRAY['Stosuje mnożniki kolejno', 'Porównuje z jednokrotną zmianą', 'Szacuje efekt łączny']),
('Rabat, VAT i napiwek – praktyczne zastosowania procentów', 'arithmetic', 'intermediate', 6, null, 'Obliczenia cen po rabatach, doliczanie podatków i napiwków.', 'Procenty – zastosowania', ARRAY['Obliczanie procentu z liczby (p% z N)'], 20, 3, ARRAY['Modeluje rabaty i podatki', 'Porównuje oferty', 'Sprawdza sensowność wyniku']),
('Mieszanie i średnie ważone – zadania z procentami', 'arithmetic', 'advanced', 6, null, 'Średnia ważona stężeń/cen; łączenie dwóch roztworów/partii.', 'Procenty – zastosowania', ARRAY['Jednostkowa cena, prędkość i gęstość – stawki jednostkowe', 'Proporcje – metoda na krzyż i uzupełnianie tabel'], 25, 4, ARRAY['Wyznacza średnie ważone', 'Stosuje tabelę mieszanek', 'Interpretuje wyniki kontekstowo']),
('Zadania tekstowe z ułamkami, proporcjami i procentami (mix)', 'arithmetic', 'advanced', 6, null, 'Zadania wieloetapowe łączące ułamki, proporcje i procenty.', 'Zastosowania – zadania problemowe', ARRAY['Dodawanie i odejmowanie ułamków o różnych mianownikach', 'Procent składany – 2–3 następujące po sobie zmiany'], 25, 4, ARRAY['Tworzy model matematyczny', 'Dobiera właściwe narzędzia', 'Weryfikuje rozwiązanie opisem']);
